mapper.select:
SELECT 

FROM check_approver a

WHERE a.id = #{id}

SELECT 

FROM check_approver a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM check_approver a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE check_approver SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
