mapper.select:
SELECT 

FROM check_punch_card a

WHERE a.id = #{id}

SELECT
FROM check_punch_card a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM check_punch_card a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE check_punch_card SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT

FROM check_punch_card a

WHERE
a.del_flag = 0
AND a.name = #{name}
AND a.punch_date = #{punchDate}

SELECT

FROM check_punch_card a

WHERE
a.del_flag = 0
AND a.number = #{number}
AND a.punch_date = #{punchDate}

mapper.delete:
DELETE FROM
check_punch_card
WHERE
	del_flag = 0
AND name = #{name}
AND punch_date = #{punchDate}
